import React, { useState } from 'react';

const CricketPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalImageSrc, setModalImageSrc] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [
    "/images/Cricket.jpg",
    "/images/Cricket.jpg",
    "/images/Cricket.jpg"
  ];

  const openModal = (index) => {
    setModalImageSrc(images[index]);
    setCurrentIndex(index);
    setIsModalOpen(true);
  };

  const closeModal = () => setIsModalOpen(false);

  const changeImage = (direction) => {
    setCurrentIndex((prevIndex) => (prevIndex + direction + images.length) % images.length);
    setModalImageSrc(images[(currentIndex + direction + images.length) % images.length]);
  };

  return (
    <div style={{ 
      backgroundColor: 'black', 
      color: 'white', 
      minHeight: '100vh', 
      padding: '20px',
      display: 'flex',
      flexDirection: 'column',
    }}>
      {images.map((src, index) => (
        <div key={index} style={{ 
          width: '100%', 
          boxSizing: 'border-box',
          padding: '20px',
          marginBottom: '20px',
        }}>
          <h1 style={{ fontSize: '2.5rem' }}>Cricket</h1>
          <p style={{ fontSize: '1.25rem' }}>
            <img 
              src={src} 
              alt="Cricket" 
              width="500vh" 
              height="340vh" 
              style={{ 
                float: index % 2 === 0 ? 'right' : 'left', 
                border: '2px dotted black', 
                margin: '0 25px 15px 0', 
                cursor: 'pointer'
              }} 
              onClick={() => openModal(index)}
            />
            In the paragraph below, the image will float to the {index % 2 === 0 ? 'right' : 'left'}. A dotted black border is added to the image. 
            We have also added margins to the image to push the text away from the image.
          </p>
        </div>
      ))}

      {isModalOpen && (
        <div style={{ 
          position: 'fixed', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          backgroundColor: 'rgba(0, 0, 0, 0.8)', 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center',
          zIndex: 1000,
        }} onClick={closeModal}>
          <div style={{ 
            position: 'relative',
            maxWidth: '90%', 
            maxHeight: '80%',
          }}>
            <img 
              src={modalImageSrc} 
              alt="Cricket" 
              style={{ 
                width: '100%', 
                height: 'auto',
                cursor: 'pointer'
              }} 
              onClick={(e) => e.stopPropagation()}
            />
            <button 
              onClick={() => changeImage(-1)} 
              style={{ 
                position: 'absolute', 
                top: '50%', 
                left: '10px', 
                transform: 'translateY(-50%)', 
                backgroundColor: 'rgba(255, 255, 255, 0.7)', 
                border: 'none', 
                padding: '10px', 
                cursor: 'pointer'
              }}
            >
              &#9664;
            </button>
            <button 
              onClick={() => changeImage(1)} 
              style={{ 
                position: 'absolute', 
                top: '50%', 
                right: '10px', 
                transform: 'translateY(-50%)', 
                backgroundColor: 'rgba(255, 255, 255, 0.7)', 
                border: 'none', 
                padding: '10px', 
                cursor: 'pointer'
              }}
            >
              &#9654;
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CricketPage;
