import React, { useState } from 'react';

const CricketPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalImageSrc, setModalImageSrc] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [
    "/images/Cricket1.jpg",
    "/images/Cricket2.jpg",
    "/images/Cricket3.jpg"
  ];

  const openModal = (index) => {
    setModalImageSrc(images[index]);
    setCurrentIndex(index);
    setIsModalOpen(true);
  };

  const closeModal = () => setIsModalOpen(false);

  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    setModalImageSrc(images[(currentIndex + 1) % images.length]);
  };

  const prevImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
    setModalImageSrc(images[(currentIndex - 1 + images.length) % images.length]);
  };

  return (
    <div style={{ 
      backgroundColor: 'black', 
      color: 'white', 
      minHeight: '100vh', // Ensure the page takes at least the full viewport height
      padding: '20px',
      display: 'flex',
      flexDirection: 'column', // Stack divs vertically
    }}>
      <div style={{ 
        width: '100%', // Full width
        boxSizing: 'border-box', // Include padding in width calculation
        padding: '20px',
        marginBottom: '20px', // Space between divs
      }}>
        <h1 style={{ fontSize: '2.5rem' }}>Cricket</h1>
        <p style={{ fontSize: '1.25rem' }}>
          <img 
            src="/images/Cricket1.jpg" 
            alt="Cricket" 
            width="500vh" 
            height="340vh" 
            style={{ 
              float: 'right', 
              border: '2px dotted black', 
              margin: '0 25px 15px 0',
              cursor: 'pointer' // Indicate that the image is clickable
            }} 
            onClick={() => openModal(0)} // Open modal with the first image
          />
          In the paragraph below, the image will float to the right. A dotted black border is added to the image. 
          We have also added margins to the image to push the text away from the image:
          0 px margin on the top and right side, 15 px margin on the bottom, and 20 px margin on the left side of the image.
        </p>
      </div>
      <div style={{ 
        width: '100%', // Full width
        boxSizing: 'border-box', // Include padding in width calculation
        padding: '20px',
        marginBottom: '20px', // Space between divs
      }}>
        <h1 style={{ fontSize: '2.5rem' }}>Cricket</h1>
        <p style={{ fontSize: '1.25rem' }}>
          <img 
            src="/images/Cricket2.jpg" 
            alt="Cricket" 
            width="500vh" 
            height="340vh" 
            style={{ 
              float: 'left', 
              border: '2px dotted black', 
              margin: '0 25px 15px 0',
              cursor: 'pointer' // Indicate that the image is clickable
            }} 
            onClick={() => openModal(1)} // Open modal with the second image
          />
          In the paragraph below, the image will float to the left. A dotted black border is added to the image. 
          We have also added margins to the image to push the text away from the image:
          0 px margin on the top and right side, 15 px margin on the bottom, and 20 px margin on the left side of the image.
        </p>
      </div>
      <div style={{ 
        width: '100%', // Full width
        boxSizing: 'border-box', // Include padding in width calculation
        padding: '20px',
      }}>
        <h1 style={{ fontSize: '2.5rem' }}>Cricket</h1>
        <p style={{ fontSize: '1.25rem' }}>
          <img 
            src="/images/Cricket3.jpg" 
            alt="Cricket" 
            width="500vh" 
            height="340vh" 
            style={{ 
              float: 'right', 
              border: '2px dotted black', 
              margin: '0 25px 15px 0',
              cursor: 'pointer' // Indicate that the image is clickable
            }} 
            onClick={() => openModal(2)} // Open modal with the third image
          />
          In the paragraph below, the image will float to the right. A dotted black border is added to the image. 
          We have also added margins to the image to push the text away from the image:
          0 px margin on the top and right side, 15 px margin on the bottom, and 20 px margin on the left side of the image.
        </p>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div style={{ 
          position: 'fixed', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          backgroundColor: 'rgba(0, 0, 0, 0.8)', 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center',
          zIndex: 1000,
        }} onClick={closeModal}>
          <div style={{ 
            position: 'relative',
            maxWidth: '90%', 
            maxHeight: '80%',
          }}>
            <img 
              src={modalImageSrc} 
              alt="Cricket" 
              style={{ 
                width: '100%', 
                height: 'auto',
                cursor: 'pointer'
              }} 
              onClick={(e) => e.stopPropagation()} // Prevent closing the modal when clicking on the image
            />
            <button 
              onClick={prevImage} 
              style={{ 
                position: 'absolute', 
                top: '50%', 
                left: '10px', 
                transform: 'translateY(-50%)', 
                backgroundColor: 'rgba(255, 255, 255, 0.7)', 
                border: 'none', 
                padding: '10px', 
                cursor: 'pointer'
              }}
            >
              &#9664; {/* Left arrow */}
            </button>
            <button 
              onClick={nextImage} 
              style={{ 
                position: 'absolute', 
                top: '50%', 
                right: '10px', 
                transform: 'translateY(-50%)', 
                backgroundColor: 'rgba(255, 255, 255, 0.7)', 
                border: 'none', 
                padding: '10px', 
                cursor: 'pointer'
              }}
            >
              &#9654; {/* Right arrow */}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CricketPage;
