import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import AOS from "aos";
import "./App.css";
import "aos/dist/aos.css";
import Header from "./components/Header";
import Footer from "./components/Footer";
import CricketPage from "./components/CricketPage";
import FootballPage from "./components/FootballPage";
import HandballPage from "./components/HandballPage";
import TennisPage from "./components/TennisPage";
import KabaddiPage from "./components/KabaddiPage";
import AthleticsPage from "./components/AthleticsPage";

const App = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 550);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    AOS.init({
      duration: 1000,
      easing: "ease-in-out",
      once: true,
    });
  }, []);

  return (
    <Router>
      <Header className={scrolled ? "header-scrolled" : ""} />
      <main className="main-content">
        <Routes>
          <Route path="/" element={
            <>
              <section id="work" style={{ padding: '50px', background: '#f4f4f4' }}>
                <h2>Work</h2>
                <p>Content about work...</p>
              </section>
              <section id="expertise" style={{ padding: '50px', background: '#e2e2e2' }}>
                <h2>Expertise</h2>
                <p>Content about expertise...</p>
              </section>
              <section id="about" style={{ padding: '50px', background: '#d0d0d0' }}>
                <h2>About</h2>
                <p>Content about about...</p>
              </section>
              <section id="contact" style={{ padding: '50px', background: '#b0b0b0' }}>
                <h2>Contact</h2>
                <p>Content about contact...</p>
              </section>
            </>
          } />
          <Route path="/cricket" element={<CricketPage />} />
          <Route path="/football" element={<FootballPage />} />
          <Route path="/handball" element={<HandballPage />} />
          <Route path="/tennis" element={<TennisPage />} />
          <Route path="/kabaddi" element={<KabaddiPage />} />
          <Route path="/athletics" element={<AthleticsPage />} />
        </Routes>
      </main>
      <Footer />
    </Router>
  );
};

export default App;
