import React from "react";

const AboutSection = () => {
  return (
    <section
      className="py-16 bg-primary-light text-white border-b-8 border-blue-300"
      id="about"
    >
      <div className="container mx-auto px-4" data-aos="fade-up">
        <div className="text-center">
          <h2 className="text-3xl font-semibold mb-4">About Us</h2>
          <p className="text-lg mb-6 pl-44 pr-44">
          We are basically a bunch of sports fans & lovers who eventually ended up making their careers in 
          the visual and sports broadcast industry. With a cumulative experience of more than 30 years, 
          we have passion, experience & industry know.
          </p>
          <p className="text-lg pl-44 pr-44">
            Design on a Dime was incorporated as a private limit company registered under the Companies Act of India, 2010.
          </p>
        </div>
      </div>
    </section>
  );
};

export default AboutSection;
