import React, { useState } from 'react';

const CricketPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalImageSrc, setModalImageSrc] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);

  // Define images in groups of 5
  const images = [
    "/images/Cricket1.jpg",
    "/images/Cricket2.jpg",
    "/images/Cricket3.jpg",
    "/images/Cricket4.jpg",
    "/images/Cricket5.jpg",
    "/images/Cricket6.jpg",
    "/images/Cricket7.jpg",
    "/images/Cricket8.jpg",
    "/images/Cricket9.jpg",
    "/images/Cricket10.jpg",
    "/images/Cricket11.jpg",
    "/images/Cricket12.jpg",
    "/images/Cricket13.jpg",
    "/images/Cricket14.jpg",
    "/images/Cricket15.jpg"
  ];

  // Handle opening the modal with the selected image
  const openModal = (index) => {
    setModalImageSrc(images[index]);
    setCurrentIndex(index);
    setIsModalOpen(true);
  };

  // Close the modal
  const closeModal = () => setIsModalOpen(false);

  // Change the image in the modal
  const changeImage = (direction) => {
    const newIndex = (currentIndex + direction + images.length) % images.length;
    setModalImageSrc(images[newIndex]);
    setCurrentIndex(newIndex);
  };

  // Render images grouped in 5 per div
  const renderImageGroups = () => {
    const imageGroups = [];
    for (let i = 0; i < images.length; i += 5) {
      imageGroups.push(
        <div key={i} style={{ 
          width: '100%', 
          boxSizing: 'border-box',
          padding: '20px',
          marginBottom: '20px',
        }}>
          <h1 style={{ fontSize: '2.5rem' }}>Cricket</h1>
          <p style={{ fontSize: '1.25rem' }}>
            {images.slice(i, i + 5).map((src, index) => (
              <img 
                key={src} 
                src={src} 
                alt={`Cricket ${i + index + 1}`} 
                width="100%" 
                height="auto" 
                style={{ 
                  border: '2px dotted black', 
                  margin: '0 25px 15px 0',
                  cursor: 'pointer'
                }} 
                onClick={() => openModal(i + index)}
              />
            ))}
          </p>
        </div>
      );
    }
    return imageGroups;
  };

  return (
    <div style={{ 
      backgroundColor: 'black', 
      color: 'white', 
      minHeight: '100vh', 
      padding: '20px',
      display: 'flex',
      flexDirection: 'column',
    }}>
      {renderImageGroups()}

      {isModalOpen && (
        <div style={{ 
          position: 'fixed', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          backgroundColor: 'rgba(0, 0, 0, 0.8)', 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center',
          zIndex: 1000,
        }} onClick={closeModal}>
          <div style={{ 
            position: 'relative',
            maxWidth: '90%', 
            maxHeight: '80%',
          }}>
            <img 
              src={modalImageSrc} 
              alt="Popup" 
              style={{ 
                width: '100%', 
                height: 'auto',
                cursor: 'pointer'
              }} 
              onClick={(e) => e.stopPropagation()}
            />
            <button 
              onClick={() => changeImage(-1)} 
              style={{ 
                position: 'absolute', 
                top: '50%', 
                left: '10px', 
                transform: 'translateY(-50%)', 
                backgroundColor: 'rgba(255, 255, 255, 0.7)', 
                border: 'none', 
                padding: '10px', 
                cursor: 'pointer'
              }}
            >
              &#9664;
            </button>
            <button 
              onClick={() => changeImage(1)} 
              style={{ 
                position: 'absolute', 
                top: '50%', 
                right: '10px', 
                transform: 'translateY(-50%)', 
                backgroundColor: 'rgba(255, 255, 255, 0.7)', 
                border: 'none', 
                padding: '10px', 
                cursor: 'pointer'
              }}
            >
              &#9654;
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CricketPage;
