import React from 'react';
import { useLocation, Link } from 'react-router-dom';

const Header = ({ className }) => {
  const location = useLocation();
  const isMainPage = location.pathname === '/';

  const handleScrollTo = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <header
      className={`fixed top-0 left-0 w-full text-white p-4 shadow-md z-50 transition-all duration-500 bg-[#005C96] ${className}`}
      aria-label="Main Navigation"
    >
      <nav className="container mx-auto flex justify-between items-center">
        <div className="text-2xl font-bold">
          <img
            src="./images/doad_logo_fulltext_white.png"
            className="max-h-10 ml-7"
            alt="Doad Logo"
          />
        </div>
        <ul className="flex space-x-4">
          {['work', 'expertise', 'about', 'contact'].map((section) => (
            <li key={section}>
              {isMainPage ? (
                <a
                  href={`#${section}`}
                  className="hover:underline focus:outline-none focus:ring-2 focus:ring-white"
                  aria-label={`Navigate to ${section.charAt(0).toUpperCase() + section.slice(1)} section`}
                >
                  {section.toUpperCase()}
                </a>
              ) : (
                <Link
                  to={`/${location.pathname.split('/')[1]}#${section}`}
                  className="hover:underline focus:outline-none focus:ring-2 focus:ring-white"
                  aria-label={`Navigate to ${section.charAt(0).toUpperCase() + section.slice(1)} section`}
                >
                  {section.toUpperCase()}
                </Link>
              )}
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
