import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import AOS from "aos";
import "./App.css";
import "aos/dist/aos.css";
import Header from "./components/Header";
import HeroSection from "./components/HeroSection";
import FeaturedProjects from "./components/FeaturedProjects";
import Expertise from "./components/Expertise";
import Testimonials from "./components/Testimonials";
import Contact from "./components/Contact";
import Footer from "./components/Footer";
import Clients from "./components/Clients";
import AboutSection from "./components/About";

// Importing sport pages
import CricketPage from "./components/CricketPage";
import FootballPage from "./components/FootballPage";
// Import other sport pages as needed

const App = () => {
  const [scrolled, setScrolled] = useState(false);

  // Handle scroll event to toggle `scrolled` state
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 550); // Adjust the scroll position threshold as needed
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Initialize AOS (Animate On Scroll)
  useEffect(() => {
    AOS.init({
      duration: 1000, // Animation duration
      easing: "ease-in-out", // Animation easing
      once: true, // Whether animation should happen only once
    });
  }, []);

  return (
    <Router>
      <div className="min-h-screen bg-gray-800 text-white">
        <Header className={scrolled ? "header-scrolled" : ""} />

        <Routes>
          <Route path="/" element={<FeaturedProjects />} />
          <Route path="/cricket" element={<CricketPage />} />
          <Route path="/football" element={<FootballPage />} />
          <Route path="/handball" element={<HandballPage />} />
          <Route path="/tennis" element={<TennisPage />} />
          <Route path="/kabaddi" element={<KabaddiPage />} />
          <Route path="/athletics" element={<AthleticsPage />} />
        </Routes>

        {/* Footer remains consistent across all pages */}
        <Footer />
      </div>
    </Router>
  );
};

export default App;
